import { useTranslation } from 'react-i18next';
import { classNames } from 'shared/lib/classNames/classNames';
import { memo } from 'react';
import { ArticleDetails } from 'entities/Article';
import { useParams } from 'react-router-dom';
import { Text } from 'shared/ui/Text/Text';
import { CommentList } from 'entities/Comment';
import cls from './ArticlesDetailPage.module.scss';

interface ArticlesDetailPageProps {
  className?: string
}

const ArticlesDetailPage = (props: ArticlesDetailPageProps) => {
  const { className } = props;
  const { t } = useTranslation('article');
  const { id } = useParams<{id: string}>();

  if (!id) {
    return (
      <div className={classNames(cls.ArticlesDetailPage, {}, [className])}>
        {t('Статья не найдена')}
      </div>
    );
  }

  return (
    <div className={classNames(cls.ArticlesDetailPage, {}, [className])}>
      <ArticleDetails id={id} />
      <Text className={cls.commentsTitle} title={t('Комментарии')} />
      <CommentList comments={[{
        id: '1',
        text: 'some text comment',
        user: { id: '1', username: 'some', avatar: 'data:image/jpeg;base64,' },
      },
      {
        id: '2',
        text: 'some text comment 2',
        user: { id: '2', username: 'some2', avatar: 'data:image/jpeg;base64,' },
      },
      ]}
      />
    </div>
  );
};

export default memo(ArticlesDetailPage);
